from pydantic import BaseModel

from agenteval.conversation_handler import ConversationHandler


class TestResult(BaseModel, arbitrary_types_allowed=True):
    """The test result for a task, which will be generated by an evaluator.

    Attributes:
        task_name: Name of the task.
        result: Description of the test result.
        reasoning: The rationale for the test result.
        success: `True` if the test passed, otherwise `False`.
        conversation_handler: The conversation handler used during evaluation.
    """

    # do not collect as a test
    __test__ = False

    task_name: str
    result: str
    reasoning: str
    success: bool
    conversation_handler: ConversationHandler
